package objectclass;

public class math {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.addExact(23,450));
        System.out.println(Math.addExact(934567,9875476));
        System.out.println(Math.decrementExact(12));
        System.out.println(Math.incrementExact(78));
        System.out.println(Math.ceil(354.90));
        System.out.println(Math.floor(456.79999));
        System.out.println(Math.sqrt(169));
        System.out.println(Math.pow(10,5));
        System.out.println(Math.PI);
        System.out.println(Math.getExponent(99999.9876));
        System.out.println(Math.log1p(123.89));
        System.out.println(Math.log10(100));
        System.out.println(Math.max(89 ,8));
        System.out.println(Math.min(786,899));
	}

}
